package com.learning.automation.javalearning;

public class Operators{

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=40;
		int y=20;
		System.out.println("Operators");
		System.out.println("First Number:"+x);
		System.out.println("Second Number:"+y);
		int z=x+y;
		System.out.println("Addtion Of Two Numbers:"+x+" "+"&"+" "+y +" is" + z);
		z=x-y;
		System.out.println("Subtraction Of Two Numbers:"+x+" "+"&"+" "+y +" is" + z);
		z=x*y;
		System.out.println("Multiplication Of Two Numbers:"+x+" "+"&"+" "+y +" is" + z);
		z=x/y;
		System.out.println("Division Of Two Numbers:"+x+" "+"&"+" "+y +" is" + z);
		z=x%y;
		System.out.println("Modulo Of Two Numbers:"+x+" "+"&"+" "+y +" is" + z);
	}

}
